// Indicar en la URL {{idComunidad}} el id de la comunidad que se desea editar
// Campos editables: {nombreComunidad, descComunidad, rutaImagen, palabrasVetadas}

{
    "nombreComunidad": "Prueba Modificación Comunidad",
    "descComunidad": "Esto es una prueba de la modificación",
    "rutaImagen": "https://ejemplo.com/imagen.png",
    "palabrasVetadas": ["vetada1","vetada2"]
}

// Permite que un miembro de la comunidad de me gusta a una publicación específica.
// Se indica en la URL el id de la publicación a la que se quiere dar me gusta {{idPublicacion}}

{
    "idUsuario": 7  // Indicar ID del usuario que quiere dar me gusta a la publicación
}

// Permite que un miembro de la comunidad quite un me gusta a una publicación específica.
// Se indica en la URL el id de la publicación a la que se quiere dar me gusta {{idPublicacion}}
// PRE: El usuario con {{idUsuario}} debe haber dado me gusta a la publicación previo a su eliminación

{
    "idUsuario": 4
}

// Permite hacer una publicación nueva en la comunidad indicada en la URL {{idComunidad}}

{
    "titulo": "Publicación de prueba",
    "contenido": "Esto es una publicación de prueba",
    "rutaFichero": null
}

// Permite actulizar los datos de una publicación específica, en una comunidad específica.
// Se indica en la URL el id de la comunidad {{idComunidad}} y el id de la publicación a consultar {{idPublicacion}}
// Campos editables: {titulo, contenido, rutaFichero}
{
    "titulo": "Publicación de prueba modificada",
    "contenido": "Esto es una prueba de modificación",
    "rutaFichero": "https://ejemplo.com/imagen.png"
}

// Permite vetar a un miembro de una comunidad específica.
// Se indica en la URL el id de la comunidad {{idComunidad}} cuyos miembro se quiere vetar. 

{
    "idUsuario": 7  // id del usuario que queremos vetar de la comunidad
}
